5:"child";a:4:{s:0:"";a:6:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:10:"Meet Orvis";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:46:"https://themeshaper.com/2016/07/07/meet-orvis/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Thu, 07 Jul 2016 15:53:13 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:2:{i:0;a:5:{s:4:"data";s:14:"Theme Launches";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:5:"Orvis";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:30:"http://themeshaper.com/?p=6607";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:717:"Introducing Orvis: a minimalist portfolio theme for photographers, designers, or visual artists. Activate Jetpack&#8217;s Custom Content Types module to display portfolio projects in a contemporary Masonry grid. Designed by Automattic&#8217;s Thomas Guillot, Orvis was inspired by the photographer wife of a colleague/friend, and named as a nod to their dog, a sweet black Lab. Take &#8230; <a href="https://themeshaper.com/2016/07/07/meet-orvis/" class="more-link">Continue reading<span class="screen-reader-text"> "Meet Orvis"</span></a><img alt="" border="0" src="https://pixel.wp.com/b.gif?host=themeshaper.com&#038;blog=45531309&#038;post=6607&#038;subd=themeshaper&#038;ref=&#038;feed=1" width="1" height="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"Kathryn P.";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:2587:"<p>Introducing <a href="https://wordpress.org/themes/orvis/"><strong>Orvis</strong></a>: a minimalist portfolio theme for photographers, designers, or visual artists. Activate<a href="https://jetpack.com/"> Jetpack&#8217;s</a> Custom Content Types module to display portfolio projects in a contemporary Masonry grid.</p>\n<p>Designed by Automattic&#8217;s <a href="https://thomasguillot.com/">Thomas Guillot</a>, Orvis was inspired by the photographer wife of a colleague/friend, and named as a nod to their dog, <a href="http://mapandmenu.com/orvis-the-dog/">a sweet black Lab</a>.</p>\n<p><a href="https://wordpress.org/themes/orvis/">Take Orvis for a ride</a> on your site today!</p>\n<p><a href="https://orvisdemo.wordpress.com/"><img data-attachment-id="6608" data-permalink="https://themeshaper.com/2016/07/07/meet-orvis/orvis-ss/" data-orig-file="https://themeshaper.files.wordpress.com/2016/07/orvis-ss.png?w=840&#038;h=730" data-orig-size="1317,1145" data-comments-opened="1" data-image-meta="{&quot;aperture&quot;:&quot;0&quot;,&quot;credit&quot;:&quot;&quot;,&quot;camera&quot;:&quot;&quot;,&quot;caption&quot;:&quot;&quot;,&quot;created_timestamp&quot;:&quot;0&quot;,&quot;copyright&quot;:&quot;&quot;,&quot;focal_length&quot;:&quot;0&quot;,&quot;iso&quot;:&quot;0&quot;,&quot;shutter_speed&quot;:&quot;0&quot;,&quot;title&quot;:&quot;&quot;,&quot;orientation&quot;:&quot;0&quot;}" data-image-title="" data-image-description="" data-medium-file="https://themeshaper.files.wordpress.com/2016/07/orvis-ss.png?w=840&#038;h=730?w=300" data-large-file="https://themeshaper.files.wordpress.com/2016/07/orvis-ss.png?w=840&#038;h=730?w=840" class="alignnone size-full wp-image-6608" src="https://themeshaper.files.wordpress.com/2016/07/orvis-ss.png?w=840&#038;h=730" alt="Orvis screenshot" width="840" height="730" srcset="https://themeshaper.files.wordpress.com/2016/07/orvis-ss.png?w=840&amp;h=730 840w, https://themeshaper.files.wordpress.com/2016/07/orvis-ss.png?w=150&amp;h=130 150w, https://themeshaper.files.wordpress.com/2016/07/orvis-ss.png?w=300&amp;h=261 300w, https://themeshaper.files.wordpress.com/2016/07/orvis-ss.png?w=768&amp;h=668 768w, https://themeshaper.files.wordpress.com/2016/07/orvis-ss.png?w=1024&amp;h=890 1024w, https://themeshaper.files.wordpress.com/2016/07/orvis-ss.png 1317w" sizes="(max-width: 709px) 85vw, (max-width: 909px) 67vw, (max-width: 1362px) 62vw, 840px" /></a></p><img alt="" border="0" src="https://pixel.wp.com/b.gif?host=themeshaper.com&#038;blog=45531309&#038;post=6607&#038;subd=themeshaper&#038;ref=&#038;feed=1" width="1" height="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:29:"http://search.yahoo.com/mrss/";a:1:{s:7:"content";a:2:{i:0;a:6:{s:4:"data";s:7:"\n			\n		";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:82:"http://2.gravatar.com/avatar/88ff758a54124a679af2e4cdaae8ba62?s=96&d=identicon&r=G";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:1:{s:29:"http://search.yahoo.com/mrss/";a:1:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:9:"kathrynwp";s:7:"attribs";a:1:{s:0:"";a:1:{s:4:"type";s:4:"html";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:1;a:6:{s:4:"data";s:7:"\n			\n		";s:7:"attribs";a:1:{s:0:"";a:2:{s:3:"url";s:59:"http://themeshaper.files.wordpress.com/2016/07/orvis-ss.png";s:6:"medium";s:5:"image";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:1:{s:29:"http://search.yahoo.com/mrss/";a:1:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:16:"Orvis screenshot";s:7:"attribs";a:1:{s:0:"";a:1:{s:4:"type";s:4:"html";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}}}}}s:27:"http://www.w3.org/2005/Atom";a:1:{s